import React, { Component } from "react";

class ImageCard extends Component {
    constructor(props) {
        super(props);

        this.imageRef = React.createRef();
    }

    componentDidMount() {
        console.log(this.imageRef.clientHeight);
    }

    render() {
        const { description, urls } = this.props.image;
        return (
            <div>
                <img
                    src={urls.regular}
                    alt={description}
                    ref={this.imageRef}
                />
            </div>
        );
    }
}

export default ImageCard;
