import React, { Component } from "react";

class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.state = {
            term: '',
        }
    }


    render() {
        return (
            <div className="ui segment">
                <form className="ui form">
                    <div className="field">
                        <label>Image Search</label>
                        <input
                            name="term"
                            type="text"
                            value={this.state.term}
                            onChange={e => {
                                this.setState({
                                    term: e.target.value
                                })
                            }}
                        />
                    </div>
                </form>
            </div>
        );
    }
}

export default SearchBar;
